prepare:
tee cxx.c <<EOF
#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>

int main(int argc, char* argv[]) {
    char arg0[] = "$CXX";
    char arg1[] = "-L$PACKAGE_WORKING_DIR/lib";

    char* args[argc + 2];

    args[0] = arg0;
    args[1] = arg1;

    for (int i = 1; i < argc; i++) {
        args[i + 1] = argv[i];
    }

    args[argc + 1] = NULL;

    execv (arg0, args);
    perror(arg0);
    return 255;
}
EOF
run $CC_FOR_BUILD $CFLAGS_FOR_BUILD $LDFLAGS_FOR_BUILD -o cxx-for-target cxx.c
export CXX="$PWD/cxx-for-target"

case $TARGET_PLATFORM_ARCH in
    *64)    CONFIGURE_EXTRA_ARGS=--enable-64bit ;;
    *)      CONFIGURE_EXTRA_ARGS=--disable-64bit ;;
esac

dopatch:
for patchfile in $(cat ../fix/patches/series)
do
    patch -p1 < ../fix/patches/$patchfile || return 1
done

if [ "$TARGET_PLATFORM_NAME" = openbsd ] ; then
    # https://www.gnu.org/software/gnulib/manual/html_node/sys_002ftimeb_002eh.html
    # https://github.com/facebook/zstd/issues/319
    mkdir sys
    cat > sys/timeb.h <<EOF
#ifndef _SYS_TIMEB_H
#define _SYS_TIMEB_H
#include <sys/time.h>
struct timeb {
    time_t          time;
    unsigned short  millitm;
    short           timezone;
    short           dstflag;
};
extern int  ftime(struct timeb*  timebuf);
#endif
EOF

    cat > ftime.c <<EOF
#include <sys/timeb.h>
#include <sys/time.h>
int ftime(struct timeb *tb)
{
    struct timeval  tv;
    struct timezone tz;
    if (gettimeofday (&tv, &tz) < 0)
        return -1;
    tb->time    = tv.tv_sec;
    tb->millitm = (tv.tv_usec + 500) / 1000;
    if (tb->millitm == 1000) {
        ++tb->time;
        tb->millitm = 0;
    }
    tb->timezone = tz.tz_minuteswest;
    tb->dstflag  = tz.tz_dsttime;
    return 0;
}
EOF

    run $CC $CFLAGS $CPPFLAGS -I. -c -o ftime.o ftime.c
    gsed -i 's|^LFLAGS2="|LFLAGS2="ftime.o |' unix/configure
fi

gsed -i -e '/-DNO_DIR/c true' -e '/-DZMEM/c true' -e "/^LFLAGS2=/c LFLAGS2='$LDFLAGS'" -e 's| != "bzip2"| = "bzip2"|' unix/configure

gsed -i '/$(RM) /s| libbz2.a||' bzip2/Makebz2.iz

run ln -s "$libbz2_INCLUDE_DIR/bzlib.h"  bzip2/
run ln -s "$libbz2_LIBRARY_DIR/libbz2.a" bzip2/

